mapper.update:
MERGE INTO CHKLST_CTG /* InspectionListManageMapper.insertOrUpdateCtg 유재원 */ USING (
SELECT #{item.ctgId} AS CTG_ID, #{item.ctgNm} AS CTG_NM, #{item.chklstId} AS CHKLST_ID, #{item.stndScore} AS STND_SCORE, #{item.ctgUseW} AS CTG_USE_W, #{item.seq} AS SEQ, (SELECT MBR_ID FROM MBR WHERE MBR_NO = #{item.creMbrId}) AS CRE_MBR_ID FROM DUAL
) temp ON (CHKLST_CTG.CTG_ID = temp.CTG_ID AND CHKLST_CTG.CHKLST_ID = temp.CHKLST_ID AND CHKLST_CTG.MASTER_CTG_ID IS NULL) WHEN MATCHED THEN UPDATE SET CTG_NM = temp.CTG_NM, STND_SCORE = temp.STND_SCORE, CTG_USE_W = temp.CTG_USE_W, SEQ = temp.SEQ, UPD_MBR_ID = temp.CRE_MBR_ID, UPD_TM = TO_CHAR(CURRENT_DATE, 'YYYYMMDDHH24MI') WHEN NOT MATCHED THEN INSERT ( CTG_ID, CTG_NM, CHKLST_ID, STND_SCORE, CTG_USE_W, SEQ, CRE_MBR_ID, CRE_TM ) VALUES ( CHKLST_CTG_SEQ.NEXTVAL, temp.CTG_NM, temp.CHKLST_ID, temp.STND_SCORE, temp.CTG_USE_W, temp.SEQ, temp.CRE_MBR_ID, TO_CHAR(CURRENT_DATE, 'YYYYMMDDHH24MI') )
