mapper.insert:
/* ProductMapper.saveProduct 원승언 */
INSERT INTO PDT(
                PDT_ID
              , BRAND_CD
              , PDT_NM
              , PDT_PRICE
              , EXP_DAYNUM
              , PDT_SELL_STTS_CD
              , CRE_MBR_ID
              , CRE_TM

) VALUES (
              PDT_SEQ.NEXTVAL
            , (SELECT
                   DTL_CD
               FROM
                   COMM_CD_DTL
               WHERE
                   DTL_CD_NM = #{brandNm}
               )
            , #{pdtNm}
            , #{pdtPrice}
            , #{expDaynum}
            , (SELECT
                   DTL_CD
               FROM
                   COMM_CD_DTL
               WHERE
                   DTL_CD_NM = #{pdtSellSttsNm}
              )
            , (SELECT
                   MBR_ID
               FROM
                   MBR
               WHERE
                   MBR_NO = #{mbrNo}
               )
            , TO_CHAR(CURRENT_DATE, 'YYYYMMDDHH24MI')
         )

mapper.update:
/* ProductMapper.updateProduct 원승언 */
UPDATE
    PDT
SET
      PDT_NM = #{pdtNm}
    , BRAND_CD = (SELECT
                      DTL_CD
                  FROM
                      COMM_CD_DTL
                  WHERE
                      DTL_CD_NM = #{brandNm}
                 )
    , PDT_PRICE = #{pdtPrice}
    , EXP_DAYNUM = #{expDaynum}
    , PDT_SELL_STTS_CD = (SELECT
                              DTL_CD
                          FROM
                              COMM_CD_DTL
                          WHERE
                              DTL_CD_NM = #{pdtSellSttsNm}
                         )
    , UPD_MBR_ID = (SELECT
                        MBR_ID
                    FROM
                        MBR
                    WHERE
                        MBR_NO = #{mbrNo}
                   )
    , UPD_TM = TO_CHAR(CURRENT_DATE, 'YYYYMMDDHH24MI')


        AND PDT_ID = #{pdtId, jdbcType=INTEGER}

/* ProductMapper.deleteProductByPdtId 원승언 */ UPDATE PDT SET PDT_STTS_W = '0'
PDT_ID IN
#{item.pdtId}
