mapper.select:
SELECT STORE_ID , BRAND_CD.DTL_CD_NM AS brandNm , STORE_NM , BRN , OPEN_HM , OWN_NM , MS.MBR_NM AS svMbrNm , MI.MBR_NM AS inspMbrNm , STORE_BSN_STTS_CD.DTL_CD_NM AS storeBsnSttsNm FROM STORE S INNER JOIN MBR MS ON S.SV_MBR_ID = MS.MBR_ID INNER JOIN MBR MI ON S.INSP_MBR_ID = MI.MBR_ID LEFT JOIN COMM_CD_DTL BRAND_CD ON BRAND_CD.DTL_CD = S.BRAND_CD LEFT JOIN COMM_CD_DTL STORE_BSN_STTS_CD ON STORE_BSN_STTS_CD.DTL_CD = S.STORE_BSN_STTS_CD
STORE_STTS_W = '1' AND STORE_BSN_STTS_CD.COMM_CD_ID = 3
AND BRAND_CD.DTL_CD = (SELECT DTL_CD FROM COMM_CD_DTL WHERE DTL_CD_NM = #{brandNm})
AND STORE_NM = #{storeNm}
AND MI.MBR_NM = #{inspMbrNm}
AND MI.MBR_NO = #{mbrNo}
ORDER BY STORE_ID DESC, STORE_BSN_STTS_CD.DTL_CD_NM DESC

SELECT DISTINCT MS.MBR_NM , MS.MBR_NO FROM MBR MS INNER JOIN STORE S ON S.SV_MBR_ID = MS.MBR_ID INNER JOIN MBR MI ON MI.MBR_ID = S.INSP_MBR_ID WHERE MS.MBR_STTS_CD = '1' AND MS.MBR_ROLE_CD = 'MR003'
AND MI.MBR_NM = #{inspMbrNm}
AND MI.MBR_NO = #{inspMbrNo}

SELECT STORE_ID , BRAND_CD.DTL_CD_NM AS brandNm , STORE_NM , BRN , BRD_PATH , STORE_ADDR , OPEN_HM , OWN_NM , OWN_TEL , MS.MBR_NM AS svMbrNm , MS.MBR_NO AS svMbrNo , MI.MBR_NM AS inspMbrNm , MI.MBR_NO AS inspMbrNo , STORE_BSN_STTS_CD.DTL_CD_NM AS storeBsnSttsNm FROM STORE S INNER JOIN MBR MS ON S.SV_MBR_ID = MS.MBR_ID INNER JOIN MBR MI ON S.INSP_MBR_ID = MI.MBR_ID LEFT JOIN COMM_CD_DTL BRAND_CD ON BRAND_CD.DTL_CD = S.BRAND_CD LEFT JOIN COMM_CD_DTL STORE_BSN_STTS_CD ON STORE_BSN_STTS_CD.DTL_CD = S.STORE_BSN_STTS_CD
STORE_BSN_STTS_CD.COMM_CD_ID = 3
AND STORE_ID = #{storeId, jdbcType=INTEGER}

mapper.insert:
INSERT INTO STORE(
                  STORE_ID
                , BRAND_CD
                , STORE_NM
                , BRN
                , BRD_PATH
                , STORE_ADDR
                , OWN_NM
                , OWN_TEL
                , SV_MBR_ID
                , INSP_MBR_ID
                , STORE_BSN_STTS_CD
                , OPEN_HM
                , LATITUDE
                , LONGITUDE
                , CRE_MBR_ID
                , CRE_TM
) VALUES (
                  STORE_SEQ.NEXTVAL
                , (SELECT
                       DTL_CD
                   FROM
                       COMM_CD_DTL
                   WHERE
                       DTL_CD_NM = #{brandNm}
                   )
                , #{storeNm}
                , #{brn}
                , #{brdPath}
                , #{storeAddr}
                , #{ownNm}
                , #{ownTel}
                , (SELECT
                       MBR_ID
                   FROM
                       MBR
                   WHERE
                       MBR_NM = #{svMbrNm}
                   AND
                       MBR_NO = #{svMbrNo}
                   )
                , (SELECT
                       MBR_ID
                   FROM
                       MBR
                   WHERE
                       MBR_NM = #{inspMbrNm}
                   AND
                       MBR_NO = #{inspMbrNo}
                   )
                , (SELECT
                       DTL_CD
                   FROM
                       COMM_CD_DTL
                   WHERE
                       DTL_CD_NM = #{storeBsnSttsNm}
                   )
                , #{openHm}
                , #{latitude}
                , #{longitude}
                , (SELECT
                       MBR_ID
                   FROM
                       MBR
                   WHERE
                       MBR_NO = #{mbrNo}
                   )
                , TO_CHAR(CURRENT_DATE, 'YYYYMMDDHH24MI')

          )

mapper.update:
UPDATE
    STORE


        BRAND_CD = (SELECT DTL_CD FROM COMM_CD_DTL WHERE DTL_CD_NM = #{brandNm})


        , STORE_NM = #{storeNm}


        , BRN = #{brn}


        , BRD_PATH = #{brdPath}


        , STORE_ADDR = #{storeAddr}


        , OWN_NM = #{ownNm}


        , OWN_TEL = #{ownTel}


        , LATITUDE = #{latitude}


        , LONGITUDE = #{longitude}


        , STORE_BSN_STTS_CD = (SELECT
                                   DTL_CD
                                FROM
                                   COMM_CD_DTL
                                WHERE
                                   DTL_CD_NM = #{storeBsnSttsNm}
                               )


        , OPEN_HM = #{openHm}


        , SV_MBR_ID = (SELECT
                          MBR_ID
                       FROM
                           MBR
                       WHERE
                           MBR_NM = #{svMbrNm}
                       AND
                          MBR_NO = #{svMbrNo}
                       )


        , INSP_MBR_ID = (SELECT
                            MBR_ID
                         FROM
                            MBR
                         WHERE
                            MBR_NM = #{inspMbrNm}
                         AND
                            MBR_NO = #{inspMbrNo}
                         )


        , UPD_MBR_ID = (SELECT
                            MBR_ID
                        FROM
                            MBR
                        WHERE
                            MBR_NO = #{mbrNo}
                        )

  , UPD_TM = TO_CHAR(CURRENT_DATE, 'YYYYMMDDHH24MI')



        AND STORE_ID = #{storeId, jdbcType=INTEGER}

UPDATE STORE SET STORE_STTS_W = '0'
STORE_ID IN
#{item.storeId}
