mapper.select:
SELECT /* InsepctionResultMapper.selectInspectionResultDetailByInspResultId 원승언 */ INRE.INSP_RESULT_ID , ST.STORE_NM , CH.CHKLST_NM , CASE WHEN ST.BRAND_CD ='B001' THEN 'KCC 크라상' WHEN ST.BRAND_CD ='B002' THEN 'KCC 도넛' WHEN ST.BRAND_CD ='B003' THEN 'KCC 브레드' WHEN ST.BRAND_CD ='B004' THEN 'KCC 카페' END AS brandNm , CASE WHEN CH.INSP_TYPE_CD ='IT001' THEN '제품점검' WHEN CH.INSP_TYPE_CD ='IT002' THEN '위생점검' WHEN CH.INSP_TYPE_CD ='IT003' THEN '정기점검' WHEN CH.INSP_TYPE_CD ='IT004' THEN '비정기점검' WHEN CH.INSP_TYPE_CD ='IT005' THEN '기획점검' END AS inspTypeNm , MB.MBR_NM , INRE.INSP_COMPL_TM , INRE.TOTAL_PENALTY , INRE.TOTAL_BSN_SSPN FROM INSP_RESULT INRE INNER JOIN INSP_SCHD INSC ON INSC.INSP_SCHD_ID = INRE.INSP_SCHD_ID INNER JOIN STORE ST ON ST.STORE_ID = INSC.STORE_ID INNER JOIN MBR MB ON MB.MBR_ID = ST.INSP_MBR_ID INNER JOIN INSP_PLAN INPL ON INPL.INSP_PLAN_ID = INSC.INSP_PLAN_ID INNER JOIN CHKLST CH ON CH.CHKLST_ID = INPL.CHKLST_ID
INRE.INSP_COMPL_W = 'Y'
AND INRE.INSP_RESULT_ID = #{inspResultId}

SELECT /* InsepctionResultMapper.selectInspectionResultSubCategoryContentByInspResultId 원승언 */ /*+ ORDERED */ DISTINCT CHCTC.CTG_ID , CHCTC.SEQ , CHCTC.CTG_NM AS SUB_CTG_NM FROM INSP_RESULT INRE INNER JOIN EVIT_ANSW EVAN ON EVAN.INSP_RESULT_ID = INRE.INSP_RESULT_ID LEFT JOIN EVIT_VLT EVVL ON EVAN.EVIT_ID = EVVL.EVIT_ID AND EVAN.INSP_RESULT_ID = EVVL.INSP_RESULT_ID INNER JOIN CHKLST_EVIT CHEV ON CHEV.EVIT_ID = EVAN.EVIT_ID INNER JOIN EVIT_CHCLST EVCH ON EVCH.EVIT_ID = EVAN.EVIT_ID INNER JOIN CHKLST_CTG CHCTC ON CHCTC.CTG_ID = CHEV.CTG_ID INNER JOIN CHKLST_CTG CHCTP ON CHCTC.MASTER_CTG_ID = CHCTP.CTG_ID
EVAN.EVIT_ANSW_CONTENT = EVCH.CHCLST_CONTENT AND INRE.INSP_COMPL_W = 'Y'
AND EVAN.INSP_RESULT_ID = #{inspResultId, jdbcType=INTEGER}
AND CHCTP.CTG_ID = #{ctgId, jdbcType=INTEGER}
ORDER BY CHCTC.SEQ

SELECT /* InsepctionResultMapper.selectInspResultEvaluationByCategoryNms 원승언 */ /*+ ORDERED */ EVAN.EVIT_ID , EVVL.VLT_ID , CHEV.EVIT_CONTENT , CHEV.EVIT_SEQ , CHEV.SCORE , EVCH.SCORE AS RECEIVED_SCORE , EVCH.PENALTY , EVCH.BSN_SSPN_DAYNUM , EVAN.EVIT_ANSW_CONTENT , CASE WHEN EVVL.VLT_PLC_CD = 'VP001' THEN '매장(홀)' WHEN EVVL.VLT_PLC_CD = 'VP002' THEN '주방' WHEN EVVL.VLT_PLC_CD = 'VP003' THEN '카페' ELSE EVVL.VLT_PLC_CD END VLT_PLC_NM , EVVL.PDT_NM_DTPLC , EVVL.VLT_CONTENT , CASE WHEN EVVL.CAUPVD_CD = 'C001' THEN '점주' WHEN EVVL.CAUPVD_CD = 'C002' THEN 'SV' WHEN EVVL.CAUPVD_CD = 'C003' THEN '직원' ELSE EVVL.CAUPVD_CD END CAUPVD_NM , EVVL.VLT_CNT , EVVL.VLT_CAUSE , EVVL.INSTRUCTION FROM INSP_RESULT INRE INNER JOIN EVIT_ANSW EVAN ON EVAN.INSP_RESULT_ID = INRE.INSP_RESULT_ID LEFT JOIN EVIT_VLT EVVL ON EVAN.EVIT_ID = EVVL.EVIT_ID AND EVAN.INSP_RESULT_ID = EVVL.INSP_RESULT_ID INNER JOIN CHKLST_EVIT CHEV ON CHEV.EVIT_ID = EVAN.EVIT_ID INNER JOIN EVIT_CHCLST EVCH ON EVCH.EVIT_ID = EVAN.EVIT_ID INNER JOIN CHKLST_CTG CHCTC ON CHCTC.CTG_ID = CHEV.CTG_ID INNER JOIN CHKLST_CTG CHCTP ON CHCTC.MASTER_CTG_ID = CHCTP.CTG_ID
EVAN.EVIT_ANSW_CONTENT = EVCH.CHCLST_CONTENT AND INRE.INSP_COMPL_W = 'Y'
AND EVAN.INSP_RESULT_ID = #{inspResultId, jdbcType=INTEGER}
AND CHCTP.CTG_ID = #{ctgId, jdbcType=INTEGER}
AND CHCTC.CTG_ID = #{subCtgId, jdbcType=INTEGER}
ORDER BY CHEV.EVIT_SEQ
