mapper.select:
SELECT /* StoreInspectionScheduleMapper.selectStoreInspectionSchedule 원승언 */ DISTINCT MB.MBR_NO AS INSP_MBR_NO , MBR_NM , CHKLST_NM , INSP_PLAN_DT , STORE_NM , CASE WHEN INSP_TYPE_CD = 'IT001' THEN '제품점검' WHEN INSP_TYPE_CD = 'IT002' THEN '위생점검' WHEN INSP_TYPE_CD = 'IT003' THEN '정기점검' WHEN INSP_TYPE_CD = 'IT004' THEN '비정기점검' WHEN INSP_TYPE_CD = 'IT005' THEN '기획점검' END AS inspTypeCd FROM MBR MB INNER JOIN STORE ST ON MB.MBR_ID = ST.INSP_MBR_ID INNER JOIN INSP_SCHD INSC ON INSC.STORE_ID = ST.STORE_ID INNER JOIN INSP_PLAN INPL ON INSC.INSP_PLAN_ID = INPL.INSP_PLAN_ID INNER JOIN CHKLST CH ON CH.CHKLST_ID = INPL.CHKLST_ID
MB.MBR_STTS_CD ='1' AND ST.STORE_BSN_STTS_CD ='1'
AND STORE_NM = #{storeNm}
AND INSP_PLAN_DT = #{inspPlanDt}
ORDER BY STORE_NM, INSP_PLAN_DT

SELECT /* StoreInspectionScheduleMapper.selectStoreInspectionScheduleByFilter 원승언 */ DISTINCT MB.MBR_NO AS INSP_MBR_NO , MB2.MBR_NO AS SV_MBR_NO , MB.MBR_NM , CHKLST_NM , INSP_PLAN_DT , STORE_NM , CASE WHEN INSP_TYPE_CD = 'IT001' THEN '제품점검' WHEN INSP_TYPE_CD = 'IT002' THEN '위생점검' WHEN INSP_TYPE_CD = 'IT003' THEN '정기점검' WHEN INSP_TYPE_CD = 'IT004' THEN '비정기점검' WHEN INSP_TYPE_CD = 'IT005' THEN '기획점검' END AS inspTypeCd FROM MBR MB INNER JOIN STORE ST ON MB.MBR_ID = ST.INSP_MBR_ID INNER JOIN MBR MB2 ON MB2.MBR_ID = ST.SV_MBR_ID INNER JOIN INSP_SCHD INSC ON INSC.STORE_ID = ST.STORE_ID INNER JOIN INSP_PLAN INPL ON INSC.INSP_PLAN_ID = INPL.INSP_PLAN_ID INNER JOIN CHKLST CH ON CH.CHKLST_ID = INPL.CHKLST_ID
MB.MBR_STTS_CD ='1' AND ST.STORE_BSN_STTS_CD ='1'
AND INSP_TYPE_CD = #{inspTypeCd}
AND MB.MBR_NO = #{inspMbrNo}
AND MB2.MBR_NO = #{svMbrNo}
ORDER BY INSP_PLAN_DT
